import React, { useState } from "react";
import { fetchOrgaoInfo } from "../api/pncpAPI";
import { InfoOrgao, InfoContrato } from "../tipos/index";

const Search: React.FC = () => {
  const [cnpj, setCnpj] = useState("");
  const [dataInicial, setDataInicial] = useState("");
  const [dataFinal, setDataFinal] = useState("");
  const [orgao, setOrgao] = useState<InfoOrgao | null>(null);
  const [contratos, setContratos] = useState<InfoContrato[]>([]);
  const [expandedIndex, setExpandedIndex] = useState<number | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const formatDateToAPI = (date: string) => {
    if (!date) return ""; // Retorna vazio se a data não estiver preenchida
    const [year, month, day] = date.split("-");
    return `${year}${month}${day}`; // Formato yyyymmdd
  };

  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    try {
      // Formata as datas para o padrão yyyymmdd
      const formattedDataInicial = formatDateToAPI(dataInicial);
      const formattedDataFinal = formatDateToAPI(dataFinal);

      // Verifica se as datas têm 8 caracteres após a formatação
      if (!formattedDataInicial || !formattedDataFinal || formattedDataInicial.length !== 8 || formattedDataFinal.length !== 8) {
        throw new Error("As datas devem estar no formato correto e não podem estar vazias.");
      }

      console.log("Chamando a API com os seguintes parâmetros:", {
        cnpj,
        dataInicial: formattedDataInicial,
        dataFinal: formattedDataFinal,
        pagina: 1,
      });

      const response = await fetchOrgaoInfo(cnpj, formattedDataInicial, formattedDataFinal, 1);

      // Verifique se a resposta possui dados
      if (response.orgao && response.contratos.length > 0) {
        setOrgao(response.orgao); // Configura o órgão
        setContratos(response.contratos); // Define todos os contratos
      } else {
        setError("Não foi possível encontrar informações para o CNPJ informado.");
      }
    } catch (err) {
      if (err instanceof Error) {
        console.error("Erro ao buscar informações do órgão:", err.message);
        setError(err.message);
      } else {
        console.error("Erro desconhecido:", err);
        setError("Erro desconhecido.");
      }
    } finally {
      setLoading(false);
    }
  };

  const toggleExpand = (index: number) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50 p-6 justify-center">
      <h1 className="text-4xl font-bold text-[#59D27C] mb-8 text-center">OrgSearch</h1>
      <div className="flex justify-center items-start">
        <div className="bg-white rounded-lg p-8 shadow-lg w-full max-w-md flex-1">
          <div className="flex flex-col">
            <input
              type="text"
              placeholder="CNPJ"
              value={cnpj}
              onChange={(e) => setCnpj(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#59D27C] transition duration-300"
            />
            <input
              type="date"
              value={dataInicial}
              onChange={(e) => setDataInicial(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#59D27C] transition duration-300"
            />
            <input
              type="date"
              value={dataFinal}
              onChange={(e) => setDataFinal(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#59D27C] transition duration-300"
            />
            <button
              onClick={handleSearch}
              className="bg-[#59D27C] text-white font-bold py-3 rounded-lg shadow hover:bg-[#45B366] transition duration-300"
            >
              Buscar contratos
            </button>
          </div>
        </div>
        <div className="flex-shrink-0 ml-14">
          <img
            src="/imagens/maosLupa.gif"
            alt="imagem busca"
            className="h-112 w-112 object-contain"
          />
        </div>
      </div>

      {loading && <p className="text-[#59D27C] mt-4 text-center">Buscando contratos...</p>} 

      {error && <p className="text-red-600 mt-4 text-center">{error}</p>}

      {orgao && (
        <div className="mt-6 bg-white p-4 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-2">Informações do Órgão</h2>
          <p className="mb-1"><strong>Razão Social:</strong> {orgao.razaoSocial}</p>
          <p className="mb-1"><strong>CNPJ:</strong> {orgao.cnpj}</p>
        </div>
      )}

      {contratos.length > 0 && (
        <div className="mt-6 bg-white p-4 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-2">Contratos</h2>
          <ul className="list-disc pl-5">
            {contratos.map((contrato, index) => (
              <li key={index} className="mb-4">
                <button
                  onClick={() => toggleExpand(index)}
                  className="text-left w-full focus:outline-none"
                >
                  <p><strong>Número:</strong> {contrato.numeroContratoEmpenho}</p>
                  <p><strong>Data Início:</strong> {contrato.dataVigenciaInicio}</p>
                  <p><strong>Data Fim:</strong> {contrato.dataVigenciaFim}</p>
                  <p><strong>Fornecedor:</strong> {contrato.nomeRazaoSocialFornecedor}</p>
                  <p><strong>Objeto:</strong> {contrato.objetoContrato}</p>
                  <p><strong>Valor:</strong> {contrato.valorInicial.toFixed(2)}</p>
                </button>
                {expandedIndex === index && (
                  <div className="mt-2 p-4 bg-gray-100 rounded-md">
                    <p><strong>Detalhes:</strong> Aqui você pode adicionar mais informações sobre o contrato, se houver.</p>
                  </div>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Search;
