import axios from "axios";
import { InfoOrgao, InfoContrato } from "../tipos/index";

export const fetchOrgaoInfo = async (cnpj: string, dataInicial: string, dataFinal: string, pagina: number) => {
    const formattedDataInicial = dataInicial.replace(/-/g, ""); // Formato yyyyMMdd
    const formattedDataFinal = dataFinal.replace(/-/g, ""); // Formato yyyyMMdd

    try {
        const response = await axios.get<{
            orgao: InfoOrgao;
            contratos: InfoContrato[];
        }>(`https://pncp.gov.br/api/consulta/v1/contratos`, {
            params: {
                cnpj,
                dataInicial: formattedDataInicial,
                dataFinal: formattedDataFinal,
                pagina
            }
        });
        return response.data;
    } catch (error) {
        console.error("Erro ao buscar informações:", error);
        throw error; // Relança o erro para ser tratado na chamada
    }
};
