import React, { useState } from "react";
import { fetchOrgaoInfo } from "../api/pncpAPI";
import { InfoOrgao, InfoContrato } from "../tipos/index";
import { AxiosError } from "axios";

const Search: React.FC = () => {
  const [cnpj, setCnpj] = useState("");
  const [dataInicial, setDataInicial] = useState("");
  const [dataFinal, setDataFinal] = useState("");
  const [orgao, setOrgao] = useState<InfoOrgao | null>(null);
  const [contratos, setContratos] = useState<InfoContrato[]>([]);
  const [expandedIndex, setExpandedIndex] = useState<number | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    try {
      const cleanedCnpj = cnpj.replace(/\D/g, "");
      if (cleanedCnpj.length !== 14) {
        throw new Error("CNPJ deve conter 14 dígitos.");
      }

      const response = await fetchOrgaoInfo(
        cleanedCnpj,
        dataInicial,
        dataFinal,
        1
      );

      if (response.orgao) {
        const cnpjRetornado = response.orgao.cnpj.replace(/\D/g, "");

        if (cnpjRetornado !== cleanedCnpj) {
          throw new Error(
            "O CNPJ buscado não corresponde ao CNPJ retornado pela API."
          );
        }

        setOrgao(response.orgao);

        const filteredContracts = response.contratos.filter((contract) => {
          const vigenciaInicio = new Date(contract.dataVigenciaInicio);
          const vigenciaFim = new Date(contract.dataVigenciaFim);
          const inicio = new Date(dataInicial);
          const fim = new Date(dataFinal);
          return vigenciaInicio >= inicio && vigenciaInicio <= fim;
        });

        setContratos(filteredContracts);
      } else {
        throw new Error(
          "Não foi possível encontrar informações para o CNPJ informado."
        );
      }
    } catch (err) {
      if (err instanceof AxiosError) {
        console.error("Erro detalhado da API:", err.response?.data);
        setError(err.response?.data?.message || "Erro desconhecido.");
      } else if (err instanceof Error) {
        setError(err.message);
      } else {
        setError("Erro desconhecido.");
      }
    } finally {
      setLoading(false);
    }
  };

  const toggleExpand = (index: number) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50 px-4 sm:px-6 md:px-8 lg:px-10 justify-center">
      <h1 className="text-6xl font-bold text-[#4CAF50] mb-6 text-center">
        OrgSearch
      </h1>
      <div className="flex flex-col md:flex-row justify-center items-start mb-4 mt-50 ">
        <div className="bg-white rounded-lg p-6 shadow-lg w-full max-w-md flex-1">
          <div className="flex flex-col w-full">
            <label className="block mb-1 text-sm text-slate-800">CNPJ:</label>
            <input
              type="text"
              placeholder="Digite o CNPJ do órgão"
              value={cnpj}
              onChange={(e) => setCnpj(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#4CAF50] transition duration-300"
            />
            <label className="block mb-1 text-sm text-slate-800">
              Data Inicial:
            </label>
            <input
              type="date"
              value={dataInicial}
              onChange={(e) => setDataInicial(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#4CAF50] transition duration-300"
            />
            <label className="block mb-1 text-sm text-slate-800">
              Data Final:
            </label>
            <input
              type="date"
              value={dataFinal}
              onChange={(e) => setDataFinal(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#4CAF50] transition duration-300"
            />
            <button
              onClick={handleSearch}
              className="bg-[#4CAF50] text-white font-bold py-3 rounded-lg shadow transition ease-in-out delay-150 bg-[#45B366] hover:-translate-y-1 hover:bg-[#45B366] duration-300"
            >
              Buscar contratos
            </button>
          </div>
        </div>
        <div className="flex-shrink-0 flex items-center mt-4 md:mt-0 md:ml-4">
          <img
            src="/imagens/FileSearching.gif"
            alt="imagem busca"
            className="h-124 w-124 object-contain"
          />
        </div>
      </div>

      {loading && (
        <p className="text-[#4CAF50] mt-2 text-center animate-bounce">
          Buscando contratos...
        </p>
      )}

      {error && <p className="text-red-600 mt-4 text-center">{error}</p>}

      {orgao && (
        
        <div className="mx-4 bg-white p-4 rounded-lg shadow-md ml-24 mr-24">
          <h2 className="text-[#4CAF50] text-2xl font-bold mb-2">
            Informações do Órgão
          </h2>
          <p className="mb-1">
            <strong>Razão Social:</strong> {orgao.razaoSocial}
          </p>
          <p className="mb-1">
            <strong>CNPJ:</strong> {orgao.cnpj}
          </p>
          <p className="mb-1">
            <strong>UF:</strong> {orgao.ufNome}
          </p>
          <p className="mb-1">
            <strong>Município:</strong> {orgao.municipioNome}
          </p>
        </div>
      )}

      {contratos.length > 0 && (
        <div className="mt-6 mx-4 bg-white p-4 rounded-lg shadow-md ml-24 mr-24 mb-6">
          <h2 className="text-[#4CAF50] text-2xl font-bold mb-2">Contratos</h2>
          {contratos.map((contrato, index) => (
            <div key={index} className="border-b p-2 border-gray-200 pb-4">
              <div
                className="flex justify-between items-center cursor-pointer"
                onClick={() => toggleExpand(index)}
              >
                <h3 className="text-lg font-semibold hover:underline">
                  {contrato.numeroContratoEmpenho}
                </h3>
                <span className="text-sm text-gray-500 ">
                  {expandedIndex === index
                    ? "Ocultar detalhes"
                    : "Mostrar detalhes"}
                </span>
              </div>
              {expandedIndex === index && (
                <div className="bg-white p-4 rounded-lg shadow-md">
                  <p>
                    <strong>Data Vigência Inicial:</strong>{" "}
                    {contrato.dataVigenciaInicio}
                  </p>
                  <p>
                    <strong>Data Vigência Final:</strong>{" "}
                    {contrato.dataVigenciaFim}
                  </p>
                  <p>
                    <strong>Fornecedor:</strong>{" "}
                    {contrato.nomeRazaoSocialFornecedor}
                  </p>
                  <p>
                    <strong>Objeto:</strong> {contrato.objetoContrato}
                  </p>
                  <p>
                    <strong>Valor Inicial:</strong>{" "}
                    {contrato.valorInicial.toLocaleString("pt-BR", {
                      style: "currency",
                      currency: "BRL",
                    })}
                  </p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Search;
