import React, { useState } from "react";
import { fetchOrgaoInfo } from "../api/pncpAPI"; // Certifique-se de que a função está configurada corretamente
import { InfoOrgao, InfoContrato } from "../tipos/index";
import { AxiosError } from "axios"; // Importação corrigida

const Search: React.FC = () => {
  const [cnpj, setCnpj] = useState("");
  const [dataInicial, setDataInicial] = useState("");
  const [dataFinal, setDataFinal] = useState("");
  const [orgao, setOrgao] = useState<InfoOrgao | null>(null);
  const [contratos, setContratos] = useState<InfoContrato[]>([]);
  const [expandedIndex, setExpandedIndex] = useState<number | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    try {
      const cleanedCnpj = cnpj.replace(/\D/g, ''); // Remove caracteres não numéricos
      if (cleanedCnpj.length !== 14) {
        throw new Error("CNPJ deve conter 14 dígitos.");
      }

      console.log("Parâmetros da requisição:", {
        cnpj: cleanedCnpj,
        dataInicial: dataInicial,
        dataFinal: dataFinal,
        pagina: 1,
      });

      const response = await fetchOrgaoInfo(cleanedCnpj, dataInicial, dataFinal, 1);

      if (response.orgao && response.contratos.length > 0) {
        setOrgao(response.orgao); // Configura o órgão

        // Filtrando os contratos de acordo com as datas de vigência
        const filteredContracts = response.contratos.filter(contract => {
          const vigenciaInicio = new Date(contract.dataVigenciaInicio);
          const vigenciaFim = new Date(contract.dataVigenciaFim);
          const inicio = new Date(dataInicial);
          const fim = new Date(dataFinal);
          return vigenciaInicio >= inicio && vigenciaInicio <= fim;
        });

        setContratos(filteredContracts); // Define os contratos filtrados
      } else {
        setError("Não foi possível encontrar informações para o CNPJ informado.");
      }
    } catch (err) {
      if (err instanceof AxiosError) {
        console.error("Erro detalhado da API:", err.response?.data); // Loga a resposta da API
        setError(err.response?.data?.message || "Erro desconhecido.");
      } else if (err instanceof Error) {
        setError(err.message);
      } else {
        setError("Erro desconhecido.");
      }
    } finally {
      setLoading(false);
    }
  };

  const toggleExpand = (index: number) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50 p-6 justify-center">
      <h1 className="text-4xl font-bold text-[#59D27C] mb-8 text-center">OrgSearch</h1>
      <div className="flex justify-center items-start">
        <div className="bg-white rounded-lg p-8 shadow-lg w-full max-w-md flex-1">
          <div className="flex flex-col">
            <input
              type="text"
              placeholder="CNPJ"
              value={cnpj}
              onChange={(e) => setCnpj(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#59D27C] transition duration-300"
            />
            <input
              type="date"
              value={dataInicial}
              onChange={(e) => setDataInicial(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#59D27C] transition duration-300"
            />
            <input
              type="date"
              value={dataFinal}
              onChange={(e) => setDataFinal(e.target.value)}
              className="border rounded-lg p-3 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#59D27C] transition duration-300"
            />
            <button
              onClick={handleSearch}
              className="bg-[#59D27C] text-white font-bold py-3 rounded-lg shadow hover:bg-[#45B366] transition duration-300"
            >
              Buscar contratos
            </button>
          </div>
        </div>
        <div className="flex-shrink-0 ml-14">
          <img
            src="/imagens/maosLupa.gif"
            alt="imagem busca"
            className="h-112 w-112 object-contain"
          />
        </div>
      </div>

      {loading && <p className="text-[#59D27C] mt-4 text-center">Buscando contratos...</p>} 

      {error && <p className="text-red-600 mt-4 text-center">{error}</p>}

      {orgao && (
        <div className="mt-6 bg-white p-4 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-2">Informações do Órgão</h2>
          <p className="mb-1"><strong>Razão Social:</strong> {orgao.razaoSocial}</p>
          <p className="mb-1"><strong>CNPJ:</strong> {orgao.cnpj}</p>
          <p className="mb-1"><strong>UF:</strong> {orgao.ufNome}</p>
          <p className="mb-1"><strong>Município:</strong> {orgao.municipioNome}</p>
        </div>
      )}

      {contratos.length > 0 && (
        <div className="mt-6 bg-white p-4 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-2">Contratos</h2>
          {contratos.map((contrato, index) => (
  <div key={index} className="border-b border-gray-200 pb-4">
    <div className="flex justify-between items-center cursor-pointer" onClick={() => toggleExpand(index)}>
      <h3 className="text-lg font-semibold">{contrato.numeroContratoEmpenho}</h3>
      <span className="text-sm text-gray-500">{expandedIndex === index ? '−' : '+'}</span>
    </div>
    {expandedIndex === index && (
      <div className="mt-2">
        <p><strong>Data Vigência Inicial:</strong> {contrato.dataVigenciaInicio}</p>
        <p><strong>Data Vigência Final:</strong> {contrato.dataVigenciaFim}</p>
        <p><strong>Fornecedor:</strong> {contrato.nomeRazaoSocialFornecedor}</p>
        <p><strong>Objeto:</strong> {contrato.objetoContrato}</p>
        <p><strong>Valor Inicial:</strong> {contrato.valorInicial.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
      </div>
    )}
  </div>
))}

        </div>
      )}
    </div>
  );
};

export default Search;
