import axios from "axios";
import { InfoOrgao, InfoContrato } from "../tipos/index";

// Função para validar o CNPJ
const isValidCnpj = (cnpj: string): boolean => {
  const cnpjRegex = /^\d{14}$/; // Verifica se possui 14 dígitos
  return cnpjRegex.test(cnpj);
};

// Função para buscar informações do órgão e contratos
export const fetchOrgaoInfo = async (
  cnpj: string,
  dataInicial: string,
  dataFinal: string,
  pagina: number
): Promise<{ orgao: InfoOrgao; contratos: InfoContrato[] }> => {
  // Verificação dos dados de entrada
  if (!isValidCnpj(cnpj)) {
    throw new Error("CNPJ inválido.");
  }

  // Validar e formatar datas
  const startDate = new Date(dataInicial);
  const endDate = new Date(dataFinal);

  if (isNaN(startDate.getTime()) || isNaN(endDate.getTime())) {
    throw new Error("Formato de data inválido. Use 'YYYY-MM-DD'.");
  }

  if (startDate > endDate) {
    throw new Error("A data inicial deve ser anterior à data final.");
  }

  // Formatando as datas no padrão esperado (YYYYMMDD)
  const formattedDataInicial = dataInicial.replace(/-/g, '');
  const formattedDataFinal = dataFinal.replace(/-/g, '');

  try {
    const response = await axios.get(`https://pncp.gov.br/api/consulta/v1/contratos`, {
      params: {
        cnpj,
        dataInicial: formattedDataInicial,
        dataFinal: formattedDataFinal,
        pagina,
      },
    });

    // Log da resposta completa
    console.log("Resposta da API:", response.data);

    // Extrair dados do órgão a partir do primeiro contrato retornado
    const orgao: InfoOrgao = {
      razaoSocial: response.data.data[0].orgaoEntidade.razaoSocial,
      cnpj: response.data.data[0].orgaoEntidade.cnpj,
      ufNome: response.data.data[0].unidadeOrgao.ufNome,
      municipioNome: response.data.data[0].unidadeOrgao.municipioNome,
    };

    // Extrair dados dos contratos
    const contratos: InfoContrato[] = response.data.data.map((contrato: any) => ({
      numeroContratoEmpenho: contrato.numeroContratoEmpenho,
      dataVigenciaInicio: contrato.dataVigenciaInicio,
      dataVigenciaFim: contrato.dataVigenciaFim,
      nomeRazaoSocialFornecedor: contrato.nomeRazaoSocialFornecedor,
      objetoContrato: contrato.objetoContrato,
      valorInicial: contrato.valorInicial,
      cnpjFornecedor: contrato.niFornecedor,
    }));

    // Verifica se o CNPJ buscado está entre os contratos retornados
    let cnpjEncontrado = contratos.some(contrato => contrato.cnpjFornecedor === cnpj);
    console.log("CNPJ buscado:", cnpj);
    console.log("CNPJ encontrado nos contratos:", contratos.map(contrato => contrato.cnpjFornecedor));

    // Paginação caso o CNPJ não seja encontrado na primeira página
    if (!cnpjEncontrado) {
      let paginaAtual = 2; // Inicia na segunda página
      const totalPaginas = response.data.totalPaginas;

      while (paginaAtual <= totalPaginas) {
        const responsePaginated = await axios.get(`https://pncp.gov.br/api/consulta/v1/contratos`, {
          params: {
            cnpj,
            dataInicial: formattedDataInicial,
            dataFinal: formattedDataFinal,
            pagina: paginaAtual,
          },
        });

        const contratosPaginated: InfoContrato[] = responsePaginated.data.data.map((contrato: any) => ({
          numeroContratoEmpenho: contrato.numeroContratoEmpenho,
          dataVigenciaInicio: contrato.dataVigenciaInicio,
          dataVigenciaFim: contrato.dataVigenciaFim,
          nomeRazaoSocialFornecedor: contrato.nomeRazaoSocialFornecedor,
          objetoContrato: contrato.objetoContrato,
          valorInicial: contrato.valorInicial,
          cnpjFornecedor: contrato.niFornecedor,
        }));

        cnpjEncontrado = contratosPaginated.some(contrato => contrato.cnpjFornecedor === cnpj);

        if (cnpjEncontrado) {
          console.log("CNPJ encontrado na página:", paginaAtual);
          contratos.push(...contratosPaginated); // Adiciona os contratos encontrados
          break; // Para a pesquisa se o CNPJ foi encontrado
        }

        paginaAtual++;
      }
    }

    if (!cnpjEncontrado) {
      console.warn("O CNPJ buscado não foi encontrado em nenhuma das páginas.");
    }

    return { orgao, contratos };
  } catch (error) {
    console.error("Erro ao buscar informações:", error);

    if (axios.isAxiosError(error)) {
      console.error("Detalhes do erro:", error.response?.data);
      throw new Error(error.response?.data?.message || "Erro ao buscar informações do órgão.");
    } else {
      throw new Error("Erro ao buscar informações do órgão.");
    }
  }
};
