import React, { useState } from 'react';
import { fetchOrgaoInfo, InfoOrgao, InfoContrato } from './pncpAPI';

const Search: React.FC = () => {
  const [cnpj, setCnpj] = useState('');
  const [dataInicial, setDataInicial] = useState('');
  const [dataFinal, setDataFinal] = useState('');
  const [orgao, setOrgao] = useState<InfoOrgao | null>(null);
  const [contratos, setContratos] = useState<InfoContrato[]>([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    setLoading(true);
    try {
      const result = await fetchOrgaoInfo(cnpj, dataInicial, dataFinal, 1);
      setOrgao(result.orgao);
      setContratos(result.contratos);
    } catch (error) {
      console.error("Erro ao realizar a busca:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Buscar Contratos</h1>
      <input
        type="text"
        value={cnpj}
        onChange={(e) => setCnpj(e.target.value)}
        placeholder="CNPJ"
      />
      <input
        type="date"
        value={dataInicial}
        onChange={(e) => setDataInicial(e.target.value)}
      />
      <input
        type="date"
        value={dataFinal}
        onChange={(e) => setDataFinal(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? 'Buscando...' : 'Buscar'}
      </button>

      {orgao && (
        <div>
          <h2>Órgão: {orgao.razaoSocial}</h2>
          {/* Renderize outras informações do órgão se necessário */}
        </div>
      )}

      {contratos.length > 0 && (
        <div>
          <h2>Contratos:</h2>
          <ul>
            {contratos.map((contrato) => (
              <li key={contrato.numeroControlePncpCompra}>
                <strong>Fornecedor:</strong> {contrato.nomeRazaoSocialFornecedor}<br />
                <strong>Objeto:</strong> {contrato.objetoContrato}<br />
                <strong>Data Início:</strong> {contrato.dataVigenciaInicio}<br />
                <strong>Data Fim:</strong> {contrato.dataVigenciaFim}<br />
                <strong>Valor Inicial:</strong> {contrato.valorInicial.toFixed(2)}<br />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Search;
