// api/pncpAPI.ts
import axios from 'axios';
import { InfoOrgao, InfoContrato } from '../tipos/index';

export const fetchOrgaoInfo = async (cnpj: string, dataInicial: string, dataFinal: string, pagina: number) => {
  const url = `https://pncp.gov.br/api/consulta/v1/contratos?cnpj=${cnpj}&dataInicial=${dataInicial}&dataFinal=${dataFinal}&pagina=${pagina}`;
  
  const response = await axios.get(url);
  
  // A resposta da API deve conter a estrutura esperada
  return {
    orgao: response.data.data.length > 0 ? response.data.data[0].orgaoEntidade : null,
    contratos: response.data.data || [],
  };
};
