// src/api/pncpAPI.ts
import axios from "axios";
import { InfoOrgao, InfoContrato } from "../tipos/index";

// Função para validar o CNPJ
const isValidCnpj = (cnpj: string): boolean => {
  const cnpjRegex = /^\d{14}$/; // Verifica se possui 14 dígitos
  return cnpjRegex.test(cnpj);
};

// Função para buscar informações do órgão e contratos
export const fetchOrgaoInfo = async (
  cnpj: string,
  dataInicial: string,
  dataFinal: string,
  pagina: number
): Promise<{ orgao: InfoOrgao; contratos: InfoContrato[] }> => {
  // Verificação dos dados de entrada
  if (!isValidCnpj(cnpj)) {
    throw new Error("CNPJ inválido.");
  }

  const startDate = new Date(dataInicial);
  const endDate = new Date(dataFinal);
  if (startDate > endDate) {
    throw new Error("A data inicial deve ser anterior à data final.");
  }

  try {
    console.log("Parâmetros da requisição:", { cnpj, dataInicial, dataFinal, pagina });

    const response = await axios.get(`https://pncp.gov.br/api/consulta/v1/contratos`, {
      params: {
        cnpj,
        dataInicial,
        dataFinal,
        pagina,
      },
    });

    // Extrair dados do órgão
    const orgao: InfoOrgao = {
      razaoSocial: response.data.data[0].orgaoEntidade.razaoSocial,
      cnpj: response.data.data[0].orgaoEntidade.cnpj,
      ufNome: response.data.data[0].unidadeOrgao.ufNome,
      municipioNome: response.data.data[0].unidadeOrgao.municipioNome,
      // Remover propriedades não utilizadas
    };

    // Extrair dados dos contratos
    const contratos: InfoContrato[] = response.data.data.map((contrato: any) => ({
      numeroContratoEmpenho: contrato.numeroContratoEmpenho,
      dataVigenciaInicio: contrato.dataVigenciaInicio,
      dataVigenciaFim: contrato.dataVigenciaFim,
      nomeRazaoSocialFornecedor: contrato.nomeRazaoSocialFornecedor,
      objetoContrato: contrato.objetoContrato,
      valorInicial: contrato.valorInicial,
    }));

    return { orgao, contratos };
  } catch (error) {
    console.error("Erro ao buscar informações:", error);

    if (axios.isAxiosError(error)) {
      console.error("Detalhes do erro:", error.response?.data);
      throw new Error(error.response?.data?.message || "Erro ao buscar informações do órgão.");
    } else {
      throw new Error("Erro ao buscar informações do órgão.");
    }
  }
};
