import axios from "axios";
import { InfoOrgao, InfoContrato } from "../tipos/index";

const url_api = "https://pncp.gov.br/api/consulta/v1/contratos";

export const fetchOrgaoInfo = async (cnpj: string, dataInicial: string, dataFinal: string, pagina: number = 1) => {
    // Formata as datas no formato yyyyMMdd
    const formattedDataInicial = dataInicial.replace(/-/g, ""); // Ex: 2024-02-09 para 20240209
    const formattedDataFinal = dataFinal.replace(/-/g, ""); // Ex: 2024-08-31 para 20240831

    try {
        // Requisição para a API
        const response = await axios.get<{
            data: {
                orgaoEntidade: InfoOrgao;
                unidadeOrgao: { ufNome: string; municipioNome: string; };
                contratos: InfoContrato[];
            }[];
            totalRegistros: number;
            totalPaginas: number;
            numeroPagina: number;
            paginasRestantes: number;
            empty: boolean;
        }>(url_api, {
            params: {
                cnpj,
                dataInicial: formattedDataInicial,
                dataFinal: formattedDataFinal,
                pagina
            },
        });

        console.log("Dados da API", response.data);

        // Verifica se há dados
        if (response.data.data.length === 0) {
            return { orgao: null, contratos: [] }; // Retorna se não houver dados
        }

        // Adquirindo informações do órgão (considerando o primeiro órgão retornado)
        const orgao = response.data.data[0].orgaoEntidade;
        const infoOrgao: InfoOrgao = {
            razaoSocial: orgao.razaoSocial,
            cnpj: orgao.cnpj,
            ufNome: response.data.data[0].unidadeOrgao.ufNome,
            municipioNome: response.data.data[0].unidadeOrgao.municipioNome,
        };

        // Adquirindo informações dos contratos
        const contratos: InfoContrato[] = response.data.data[0].contratos.map((item) => ({
            numeroContratoEmpenho: item.numeroContratoEmpenho,
            dataVigenciaInicio: item.dataVigenciaInicio,
            dataVigenciaFim: item.dataVigenciaFim,
            nomeRazaoSocialFornecedor: item.nomeRazaoSocialFornecedor,
            objetoContrato: item.objetoContrato,
            valorInicial: item.valorInicial,
        }));

        // Retorna as informações do órgão e contratos
        return { orgao: infoOrgao, contratos };

    } catch (error: unknown) {
        console.error("Erro na busca de dados do órgão!", error);
        if (axios.isAxiosError(error)) {
            console.error("Dados do erro:", error.response?.data);
        } else {
            console.error("Erro desconhecido:", error);
        }
        return null; // Retorna nulo em caso de erro
    }
};
