// Importa as dependências necessárias do React e outras bibliotecas
import React, { useState } from "react";
import { fetchOrgaoInfo } from "../api/pncpAPI"; // Função para buscar informações do órgão
import { InfoOrgao, InfoContrato } from "../tipos/index"; // Tipos para as informações do órgão e contratos
import { AxiosError } from "axios"; // Importa o tipo de erro do Axios

// Componente funcional para buscar informações de contratos
const Search: React.FC = () => {
  // Estado para armazenar valores de entrada e dados do órgão e contratos
  const [cnpj, setCnpj] = useState(""); // CNPJ do órgão a ser buscado
  const [dataInicial, setDataInicial] = useState(""); // Data inicial da busca
  const [dataFinal, setDataFinal] = useState(""); // Data final da busca
  const [orgao, setOrgao] = useState<InfoOrgao | null>(null); // Informações do órgão encontrado
  const [contratos, setContratos] = useState<InfoContrato[]>([]); // Lista de contratos encontrados
  const [expandedIndex, setExpandedIndex] = useState<number | null>(null); // Índice do contrato expandido para mostrar detalhes
  const [loading, setLoading] = useState(false); // Estado de carregamento da busca
  const [error, setError] = useState<string | null>(null); // Mensagem de erro, se houver
  const [contratosSomados, setValorTotal] = useState<number | null>(null); // Valor total dos contratos encontrados

  // Função para lidar com a busca de contratos
  const handleSearch = async () => {
    setLoading(true); // Inicia o estado de carregamento
    setError(null); // Reseta mensagens de erro

    try {
      const cnpjLimpo = cnpj.replace(/\D/g, ""); // Remove caracteres não numéricos do CNPJ
      if (cnpjLimpo.length !== 14) {
        // Verifica se o CNPJ tem 14 dígitos
        throw new Error("CNPJ deve conter 14 dígitos.");
      }

      // Busca as informações do órgão pela API
      const response = await fetchOrgaoInfo(
        cnpjLimpo,
        dataInicial,
        dataFinal,
        1
      );

      // Verifica se o órgão foi encontrado na resposta
      if (response.orgao) {
        const cnpjRetornado = response.orgao.cnpj.replace(/\D/g, ""); // Obtém o CNPJ retornado da API

        // Verifica se o CNPJ buscado corresponde ao retornado
        if (cnpjRetornado !== cnpjLimpo) {
          throw new Error(
            "O CNPJ buscado não corresponde ao CNPJ retornado pela API."
          );
        }

        setOrgao(response.orgao); // Atualiza o estado com as informações do órgão

        // Filtra os contratos pela data de vigência
        const contratosEncontrados = response.contratos.filter((contract) => {
          const vigenciaInicio = new Date(contract.dataVigenciaInicio);
          const vigenciaFim = new Date(contract.dataVigenciaFim);
          const inicio = new Date(dataInicial);
          const fim = new Date(dataFinal);
          return vigenciaInicio >= inicio && vigenciaInicio <= fim; // Verifica se a data de vigência inicial está dentro do intervalo
        });

        setContratos(contratosEncontrados); // Atualiza o estado com os contratos encontrados

        // Calcula o valor total dos contratos encontrados
        const contratosSomados = contratosEncontrados.reduce(
          (sum, contrato) => sum + contrato.valorInicial,
          0
        );

        setValorTotal(contratosSomados); // Atualiza o estado com o valor total
      } else {
        throw new Error(
          "Não foi possível encontrar informações para o CNPJ informado."
        );
      }
    } catch (err) {
      // Trata erros ocorridos durante a busca
      if (err instanceof AxiosError) {
        console.error("Erro detalhado da API:", err.response?.data);
        setError(err.response?.data?.message || "Erro desconhecido.");
      } else if (err instanceof Error) {
        setError(err.message); // Define a mensagem de erro
      } else {
        setError("Erro desconhecido.");
      }
    } finally {
      setLoading(false); // Finaliza o estado de carregamento
    }
  };

  // Função para salvar contratos no banco de dados
 // Função para salvar contratos no banco de dados e baixar o arquivo
 const salvarContratoNoBD = async (
  orgao: InfoOrgao,
  contratos: InfoContrato[],
  contratosSomados: number
) => {
  try {
    // Envia os dados para salvar no banco de dados
    await fetch("http://localhost:5000/api/contratos", {
      method: "POST", // Usando POST
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ orgao, contratos }), // Envia os dados como JSON
    });

    // Faz o download do banco de dados
    const response = await fetch("http://localhost:5000/api/download");
    if (!response.ok) {
      throw new Error("Erro ao baixar o arquivo");
    }
    
    // Cria um link temporário para o download
    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "contratos.db"; // Nome do arquivo que será baixado
    document.body.appendChild(a);
    a.click();
    a.remove();
    window.URL.revokeObjectURL(url); // Libera o objeto URL criado
  } catch (error) {
    console.error("Erro ao salvar contratos no banco de dados:", error);
  }
};

  // Função para alternar a visualização dos detalhes do contrato
  const toggleExpand = (index: number) => {
    setExpandedIndex(expandedIndex === index ? null : index); // Alterna o estado do índice expandido
  };

  // Retorno do componente, incluindo JSX para renderização
  return (
    <div className="flex flex-col min-h-screen bg-gray-50 px-2 sm:px-6 md:px-8 lg:px-10 justify-center">
      {/* Logo do sistema */}
      <div className="flex-shrink-0 flex items-center justify-center md:mt-1 md:ml-4">
        <img
          src="/imagens/OrgSearchLogo.png"
          alt="imagem logo"
          className="h-32 w-96 object-contain"
        />
      </div>
      {/* Formulário de busca */}
      <div className="flex flex-col md:flex-row justify-center items-start">
        <div className="bg-white rounded-lg p-6 shadow-lg w-full max-w-md mx-6 my-14 flex-1 md:ml-28 ml-20">
          <div className="flex flex-col w-full">
            <label className="block mb-1 text-sm text-slate-800">CNPJ:</label>
            <input
              type="text"
              placeholder="XX.XXX.XXX/0001-XX"
              value={cnpj}
              onChange={(e) => setCnpj(e.target.value)} // Atualiza o estado do CNPJ
              className="border rounded-lg p-4 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#4CAF50] transition duration-300"
            />
            <label className="block mb-1 text-sm text-slate-800">
              Data Inicial:
            </label>
            <input
              type="date"
              value={dataInicial}
              onChange={(e) => setDataInicial(e.target.value)} // Atualiza o estado da data inicial
              className="border rounded-lg p-4 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#4CAF50] transition duration-300"
            />
            <label className="block mb-1 text-sm text-slate-800">
              Data Final:
            </label>
            <input
              type="date"
              value={dataFinal}
              onChange={(e) => setDataFinal(e.target.value)} // Atualiza o estado da data final
              className="border rounded-lg p-4 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#4CAF50] transition duration-300"
            />
            <button
              onClick={handleSearch} // Chama a função de busca ao clicar
              className="bg-[#4CAF50] text-white font-bold py-4 rounded-lg shadow transition ease-in-out delay-150 bg-[#45B366] hover:-translate-y-1 hover:bg-[#45B366] duration-100"
            >
              Buscar contratos
            </button>
          </div>
        </div>
        <div className="flex-shrink-0 flex items-center md:mt-1 md:ml-15 ml-14">
          <img
            src="/imagens/FileSearching.gif"
            alt="imagem busca"
            className="h-5/6 w-5/6 object-contain"
          />
        </div>
      </div>

      {/* Mensagem de carregamento */}
      {loading && (
        <p className="text-[#4CAF50] text-center animate-bounce ease-out duration-75">
          Buscando contratos...
        </p>
      )}
      

      {/* Mensagem de erro, se houver */}
      {error && <p className="text-red-600 mt-1 text-center">{error}</p>}


      {/* Exibe informações do órgão, se disponíveis */}
      {orgao && (
        
        <div className="mx-24 bg-white p-4 rounded-lg shadow-md">
         

          <h2 className="text-[#4CAF50] text-2xl font-bold mb-6">
            Informações do Órgão
          </h2>

          <p className="mb-1">
            <strong>Razão Social:</strong> {orgao.razaoSocial}
          </p>
          <p className="mb-1">
            <strong>CNPJ:</strong> {orgao.cnpj}
          </p>
          <p className="mb-1">
            <strong>UF:</strong> {orgao.ufNome}
          </p>
          <p className="mb-1">
            <strong>Município:</strong> {orgao.municipioNome}
          </p>
          {contratosSomados !== null && (
            <p className="text-lg font-bold text-center mt-4">
              Valor Total dos Contratos:{" "}
              {contratosSomados.toLocaleString("pt-BR", {
                style: "currency",
                currency: "BRL",
              })}
            </p>
          )}
           <button
            onClick={() => {
              salvarContratoNoBD(orgao, contratos, contratosSomados!); // Chama a função de salvar ao clicar
            }}
            className="bg-[#4CAF50] text-white font-bold py-2 px-4 rounded-lg shadow transition ease-in-out delay-150 bg-[#45B366] hover:-translate-y-1 hover:bg-[#45B366] duration-100"
            disabled={!orgao || contratos.length === 0} // Desabilita o botão se não houver órgão ou contratos
          >
            Download
          </button>
        </div>
        
      )}

      {/* Exibe a lista de contratos, se houver */}
      
      {contratos.length > 0 && (
        <div className="mt-6 mx-24 bg-white p-4 rounded-lg shadow-md mb-6">
          
          <h2 className="text-[#4CAF50] text-2xl font-bold mb-6">Contratos</h2>
          
          
          {contratos.map((contrato, index) => (
            <div key={index} className="border-b p-2 border-gray-200 pb-4">
              <div
                className="flex justify-between items-center cursor-pointer"
                onClick={() => toggleExpand(index)} // Alterna a visualização dos detalhes do contrato
              >
                <h3 className="text-lg font-semibold hover:underline">
                  {contrato.numeroContratoEmpenho}
                </h3>
                <span className="text-sm text-gray-500 ">
                  {expandedIndex === index
                    ? "Ocultar detalhes"
                    : "Mostrar detalhes"}
                </span>
              </div>
              {/* Detalhes do contrato, se expandido */}
              {expandedIndex === index && (
                <div className="bg-white p-4 rounded-lg shadow-md">
                  <p>
                    <strong>Data Vigência Inicial:</strong>{" "}
                    {contrato.dataVigenciaInicio}
                  </p>
                  <p>
                    <strong>Data Vigência Final:</strong>{" "}
                    {contrato.dataVigenciaFim}
                  </p>
                  <p>
                    <strong>Fornecedor:</strong>{" "}
                    {contrato.nomeRazaoSocialFornecedor}
                  </p>
                  <p>
                    <strong>Objeto:</strong> {contrato.objetoContrato}
                  </p>
                  <p>
                    <strong>Valor Inicial:</strong>{" "}
                    {contrato.valorInicial.toLocaleString("pt-BR", {
                      style: "currency",
                      currency: "BRL",
                    })}
                  </p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
      <div>
        {/* Rodapé */}
        <footer className="  text-xs text-gray-500 text-center m-full mt-auto">
          <p>
            © 2024 OrgSearch. Todos os direitos reservados. Desenvolvido por{" "}
            <a href="https://www.linkedin.com/in/tulianandrade/" className="underline">
              Tuliana Andrade
            </a>
          </p>
        </footer>
      </div>
    </div>
  );
};

// Exporta o componente para uso em outras partes da aplicação
export default Search;
