import React, { useState } from "react";
import { fetchOrgaoInfo } from "../api/pncpAPI";
import { InfoOrgao, InfoContrato } from "../tipos";

const Search: React.FC = () => {
    const [cnpj, setCnpj] = useState("");
    const [dataInicial, setDataInicial] = useState("");
    const [dataFinal, setDataFinal] = useState("");
    const [orgao, setOrgao] = useState<InfoOrgao | null>(null);
    const [contratos, setContratos] = useState<InfoContrato[]>([]);
    const [loading, setLoading] = useState(false);  // Estado de carregamento
    const [error, setError] = useState<string | null>(null);

    const handleSearch = async () => {
        setLoading(true);  // Inicia o carregamento
        setError(null);    // Limpa erros anteriores
        try {
            const response = await fetchOrgaoInfo(cnpj, dataInicial, dataFinal);
            if (response.orgao) {
                setOrgao(response.orgao);
                setContratos(response.contratos);
            } else {
                setError("Não foi possível encontrar informações para o CNPJ informado.");
            }
        } catch (error) {
            setError("Ocorreu um erro na busca dos dados.");
        } finally {
            setLoading(false);  // Finaliza o carregamento
        }
    };

    return (
        <div>
            <input
                type="text"
                value={cnpj}
                onChange={(e) => setCnpj(e.target.value)}
                placeholder="Informe o CNPJ"
            />
            <input
                type="date"
                value={dataInicial}
                onChange={(e) => setDataInicial(e.target.value)}
            />
            <input
                type="date"
                value={dataFinal}
                onChange={(e) => setDataFinal(e.target.value)}
            />
            <button onClick={handleSearch}>Buscar</button>

            {loading && <p>Buscando dados...</p>}  {/* Indicador de carregamento */}

            {error && <p style={{ color: "red" }}>{error}</p>}

            {orgao && (
                <div>
                    <h2>Dados do Órgão</h2>
                    <p>Razão Social: {orgao.razaoSocial}</p>
                    <p>CNPJ: {orgao.cnpj}</p>
                    <p>UF: {orgao.ufNome}</p>
                    <p>Município: {orgao.municipioNome}</p>
                </div>
            )}

            {contratos.length > 0 && (
                <div>
                    <h2>Contratos</h2>
                    {contratos.map((contrato, index) => (
                        <div key={index}>
                            <p>Número do Empenho: {contrato.numeroContratoEmpenho}</p>
                            <p>Vigência: {contrato.dataVigenciaInicio} a {contrato.dataVigenciaFim}</p>
                            <p>Fornecedor: {contrato.nomeRazaoSocialFornecedor}</p>
                            <p>Objeto: {contrato.objetoContrato}</p>
                            <p>Valor Inicial: {contrato.valorInicial}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Search;
