// pncpAPI.ts
import axios from "axios";
import { InfoOrgao, InfoContrato } from "../tipos/index";

const fetchOrgaoInfo = async (cnpj: string, dataInicial: string, dataFinal: string, pagina: number) => {
    const response = await axios.get(`https://pncp.gov.br/api/consulta/v1/contratos?cnpj=${cnpj}&dataInicial=${dataInicial}&dataFinal=${dataFinal}&pagina=${pagina}`);
    
    const orgao: InfoOrgao = {
        razaoSocial: response.data.data[0].orgaoEntidade.razaoSocial,
        cnpj: response.data.data[0].orgaoEntidade.cnpj,
        poderId: response.data.data[0].orgaoEntidade.poderId,
        esferaId: response.data.data[0].orgaoEntidade.esferaId,
        ufNome: response.data.data[0].unidadeOrgao.ufNome, // Certifique-se de que isso existe
        municipioNome: response.data.data[0].unidadeOrgao.municipioNome // E isso também
    };

    const contratos: InfoContrato[] = response.data.data.map((contrato: any) => ({
        numeroContratoEmpenho: contrato.numeroContratoEmpenho,
        dataVigenciaInicio: contrato.dataVigenciaInicio,
        dataVigenciaFim: contrato.dataVigenciaFim,
        nomeRazaoSocialFornecedor: contrato.nomeRazaoSocialFornecedor,
        objetoContrato: contrato.objetoContrato,
        valorInicial: contrato.valorInicial,
    }));

    return { orgao, contratos };
};

export default fetchOrgaoInfo;
