import React, { useState } from 'react';
import axios from 'axios';

// Definição das interfaces para os dados do órgão e contratos
interface InfoOrgao {
  cnpj: string;
  razaoSocial: string;
}

interface InfoContrato {
  numeroControlePncpCompra: string;
  dataAssinatura: string;
  dataVigenciaInicio: string;
  dataVigenciaFim: string;
  nomeRazaoSocialFornecedor: string;
  valorInicial: number;
  objetoContrato: string;
}

// Função para buscar contratos pelo CNPJ
const fetchContratosByCnpj = async (cnpj: string, dataInicial: string, dataFinal: string, pagina: number): Promise<{ orgao: InfoOrgao; contratos: InfoContrato[] }> => {
  try {
    const response = await axios.get(`https://pncp.gov.br/api/consulta/v1/contratos`, {
      params: {
        cnpj,
        dataInicial,
        dataFinal,
        pagina,
      },
    });

    const orgao = response.data.data[0]?.orgaoEntidade; // Primeiro órgão encontrado
    const contratos = response.data.data.map((contrato: any) => ({
      numeroControlePncpCompra: contrato.numeroControlePncpCompra,
      dataAssinatura: contrato.dataAssinatura,
      dataVigenciaInicio: contrato.dataVigenciaInicio,
      dataVigenciaFim: contrato.dataVigenciaFim,
      nomeRazaoSocialFornecedor: contrato.nomeRazaoSocialFornecedor,
      valorInicial: contrato.valorInicial,
      objetoContrato: contrato.objetoContrato,
    }));

    return { orgao, contratos };
  } catch (error) {
    console.error("Erro ao buscar informações:", error);

    if (axios.isAxiosError(error)) {
      console.error("Detalhes do erro:", error.response?.data);
      throw new Error(error.response?.data?.message || "Erro ao buscar informações do órgão.");
    } else {
      throw new Error("Erro ao buscar informações do órgão.");
    }
  }
};

// Componente principal
const OrgSearch: React.FC = () => {
  const [cnpj, setCnpj] = useState<string>('');
  const [dataInicial, setDataInicial] = useState<string>('20240701');
  const [dataFinal, setDataFinal] = useState<string>('20240901');
  const [orgao, setOrgao] = useState<InfoOrgao | null>(null);
  const [contratos, setContratos] = useState<InfoContrato[]>([]);
  const [error, setError] = useState<string>('');

  const handleSearch = async () => {
    try {
      const { orgao, contratos } = await fetchContratosByCnpj(cnpj, dataInicial, dataFinal, 1);
      setOrgao(orgao);
      setContratos(contratos);
      setError(''); // Limpa qualquer erro anterior
    } catch (err: any) {
      setError(err.message);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-xl font-bold mb-4">Busca de Contratos por Órgão</h1>
      <input
        type="text"
        value={cnpj}
        onChange={(e) => setCnpj(e.target.value)}
        placeholder="CNPJ do Órgão"
        className="border p-2 mb-4"
      />
      <button onClick={handleSearch} className="bg-blue-500 text-white p-2">Buscar</button>

      {error && <p className="text-red-500 mt-2">{error}</p>}

      {orgao && (
        <div className="mt-4">
          <h2 className="text-lg font-bold">Órgão Encontrado:</h2>
          <p><strong>CNPJ:</strong> {orgao.cnpj}</p>
          <p><strong>Razão Social:</strong> {orgao.razaoSocial}</p>
        </div>
      )}

      {contratos.length > 0 && (
        <div className="mt-4">
          <h2 className="text-lg font-bold">Contratos Associados:</h2>
          <ul>
            {contratos.map((contrato, index) => (
              <li key={index} className="border p-2 mb-2">
                <p><strong>Número Controle:</strong> {contrato.numeroControlePncpCompra}</p>
                <p><strong>Data de Assinatura:</strong> {contrato.dataAssinatura}</p>
                <p><strong>Data Vigência Início:</strong> {contrato.dataVigenciaInicio}</p>
                <p><strong>Data Vigência Fim:</strong> {contrato.dataVigenciaFim}</p>
                <p><strong>Fornecedor:</strong> {contrato.nomeRazaoSocialFornecedor}</p>
                <p><strong>Valor Inicial:</strong> {contrato.valorInicial}</p>
                <p><strong>Objeto do Contrato:</strong> {contrato.objetoContrato}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default OrgSearch;
