import React, { useState } from "react";
import { fetchOrgaoInfo } from "../api/pncpAPI";
import { InfoOrgao, InfoContrato } from "../tipos/index";
import { AxiosError } from "axios";

const Search: React.FC = () => {
  const [cnpj, setCnpj] = useState("");
  const [dataInicial, setDataInicial] = useState("");
  const [dataFinal, setDataFinal] = useState("");
  const [orgao, setOrgao] = useState<InfoOrgao | null>(null);
  const [contratos, setContratos] = useState<InfoContrato[]>([]);
  const [expandedIndex, setExpandedIndex] = useState<number | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    try {
      const cleanedCnpj = cnpj.replace(/\D/g, "");
      if (cleanedCnpj.length !== 14) {
        throw new Error("CNPJ deve conter 14 dígitos.");
      }

      const response = await fetchOrgaoInfo(
        cleanedCnpj,
        dataInicial,
        dataFinal,
        1
      );

      if (response.orgao) {
        const cnpjRetornado = response.orgao.cnpj.replace(/\D/g, "");

        if (cnpjRetornado !== cleanedCnpj) {
          throw new Error(
            "O CNPJ buscado não corresponde ao CNPJ retornado pela API."
          );
        }

        setOrgao(response.orgao);

        const filteredContracts = response.contratos.filter((contract) => {
          const vigenciaInicio = new Date(contract.dataVigenciaInicio);
          const vigenciaFim = new Date(contract.dataVigenciaFim);
          const inicio = new Date(dataInicial);
          const fim = new Date(dataFinal);
          return vigenciaInicio >= inicio && vigenciaInicio <= fim;
        });

        setContratos(filteredContracts);
      } else {
        throw new Error(
          "Não foi possível encontrar informações para o CNPJ informado."
        );
      }
    } catch (err) {
      if (err instanceof AxiosError) {
        console.error("Erro detalhado da API:", err.response?.data);
        setError(err.response?.data?.message || "Erro desconhecido.");
      } else if (err instanceof Error) {
        setError(err.message);
      } else {
        setError("Erro desconhecido.");
      }
    } finally {
      setLoading(false);
    }
  };

  const toggleExpand = (index: number) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50 px-4 sm:px-6 md:px-8 lg:px-10 justify-center">
      <div className="flex-shrink-0 flex items-center justify-center md:mt-1 md:ml-4">
        <img
          src="/imagens/OrgSearchLogo.png"
          alt="imagem logo"
          className="h-96 w-96 object-contain"
        />
      </div>
      <div className="flex flex-col md:flex-row justify-center items-start my-6">
        <div className="bg-white rounded-lg p-6 shadow-lg w-full max-w-md mx-6 my-20 flex-1">
          <div className="flex flex-col w-full">
            <label className="block mb-1 text-sm text-slate-800">CNPJ:</label>
            <input
              type="text"
              placeholder="Digite o CNPJ do órgão"
              value={cnpj}
              onChange={(e) => setCnpj(e.target.value)}
              className="border rounded-lg p-4 mb-4 w-full focus:outline-none focus:ring-2 focus:ring-[#4CAF50] transition duration-300"
            />
            <label className="block mb-1 text-sm text-slate-800">Data Inicial:</label>
            <div className="relative max-w-sm mb-4">
              <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
                <svg className="w-4 h-4 text-gray-500" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M20 4a2 2 0 0 0-2-2h-2V1a1 1 0 0 0-2 0v1h-3V1a1 1 0 0 0-2 0v1H6V1a1 1 0 0 0-2 0v1H2a2 2 0 0 0-2 2v2h20V4ZM0 18a2 2 0 0 0 2 2h16a2 2 0 0 0 2-2V8H0v10Zm5-8h10a1 1 0 0 1 0 2H5a1 1 0 0 1 0-2Z"/>
                </svg>
              </div>
              <input
                id="datepicker-inicial"
                type="date"
                value={dataInicial}
                onChange={(e) => setDataInicial(e.target.value)}
                className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-[#4CAF50] focus:border-[#4CAF50] block w-full ps-10 p-2.5"
                placeholder="Selecione a data"
              />
            </div>
            <label className="block mb-1 text-sm text-slate-800">Data Final:</label>
            <div className="relative max-w-sm mb-4">
              <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
                <svg className="w-4 h-4 text-gray-500" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M20 4a2 2 0 0 0-2-2h-2V1a1 1 0 0 0-2 0v1h-3V1a1 1 0 0 0-2 0v1H6V1a1 1 0 0 0-2 0v1H2a2 2 0 0 0-2 2v2h20V4ZM0 18a2 2 0 0 0 2 2h16a2 2 0 0 0 2-2V8H0v10Zm5-8h10a1 1 0 0 1 0 2H5a1 1 0 0 1 0-2Z"/>
                </svg>
              </div>
              <input
                id="datepicker-final"
                type="date"
                value={dataFinal}
                onChange={(e) => setDataFinal(e.target.value)}
                className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-[#4CAF50] focus:border-[#4CAF50] block w-full ps-10 p-2.5"
                placeholder="Selecione a data"
              />
            </div>
            <button
              onClick={handleSearch}
              className="bg-[#4CAF50] text-white font-bold py-4 rounded-lg shadow transition ease-in-out delay-150 bg-[#45B366] hover:-translate-y-1 hover:bg-[#45B366] duration-300"
            >
              Buscar contratos
            </button>
          </div>
        </div>
        <div className="flex-shrink-0 flex items-center md:mt-1 md:ml-4">
          <img
            src="/imagens/FileSearching.gif"
            alt="imagem busca"
            className="h-full w-full object-contain"
          />
        </div>
      </div>

      {loading && (
        <p className="text-[#4CAF50] text-center animate-bounce ease-out duration-75">
          Buscando contratos...
        </p>
      )}

      {error && <p className="text-red-600 mt-1 text-center">{error}</p>}

      {orgao && (
        <div className="mx-4 bg-white p-4 rounded-lg shadow-md">
          <h2 className="text-[#4CAF50] text-2xl font-bold mb-6">Informações do Órgão</h2>
          <p className="mb-1"><strong>Razão Social:</strong> {orgao.razaoSocial}</p>
          <p className="mb-1"><strong>CNPJ:</strong> {orgao.cnpj}</p>
          <p className="mb-1"><strong>UF:</strong> {orgao.ufNome}</p>
          <p className="mb-1"><strong>Tipo:</strong> {orgao.tipo}</p>
          <p className="mb-1"><strong>Data da Última Atualização:</strong> {orgao.dataAtualizacao}</p>
        </div>
      )}

      {contratos.length > 0 && (
        <div className="mx-4 mt-4 bg-white p-4 rounded-lg shadow-md">
          <h2 className="text-[#4CAF50] text-2xl font-bold mb-6">Contratos</h2>
          {contratos.map((contrato, index) => (
            <div key={contrato.id} className="mb-4">
              <div
                className="cursor-pointer bg-gray-100 p-4 rounded-lg hover:bg-gray-200 transition duration-300"
                onClick={() => toggleExpand(index)}
              >
                <h3 className="text-lg font-semibold">{contrato.razaoSocialFornecedor}</h3>
                <p>
                  Data de Vigência Inicial: {contrato.dataVigenciaInicio} | Valor: {contrato.valorInicial}
                </p>
                {expandedIndex === index && (
                  <div className="mt-2">
                    <p><strong>Objeto:</strong> {contrato.objeto}</p>
                    <p><strong>Data de Vigência Final:</strong> {contrato.dataVigenciaFim}</p>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Search;
