import axios from 'axios';
import {InfoOrgao, InfoContrato} from '../tipos/index';

export const fetchOrgaoInfo = async (cnpj: string, dataInicial: string, dataFinal: string) => {
    try {
        // requisição para a api
        const resposta = await axios.get(`/orgaos`, { 
            params: {
                cnpj,
                dataInicial,
                dataFinal,
            },
        });

        //adquirindo informações do órgão
        const orgao: InfoOrgao = {
            razaoSocial: resposta.data.orgao.razaoSocial,
            cnpj: resposta.data.orgao.cnpj,
            ufNome: resposta.data.orgao.ufNome,
            municipioNome: resposta.data.orgao.municipioNome,
        };
        
        //adquirindo informações dos contratos
        const contratos: InfoContrato[] = resposta.data.contratos.map((contrato: any) => ({
            numeroContratoEmpenho: contrato.numeroContratoEmpenho,
            dataVigenciaInicio: contrato.dataVigenciaInicio,
            dataVigenciaFim: contrato.dataVigenciaFim,
            nomeRazaoSocialFornecedor: contrato.nomeRazaoSocialFornecedor,
            objetoContrato: contrato.objetoContrato,
            valorInicial: contrato.valorInicial,
          }));

        //retorna as informações de contratos e órgãos
        return{orgao, contratos};

        } catch (error) {
            console.error('Erro na busca de dados do órgão!', error);
            return null;
        }
        };
